import React, { useState, useEffect } from 'react';
import './index.css';

function App() {
  const [image, setImage] = useState(null);
  const [adviceType, setAdviceType] = useState('');
  const [result, setResult] = useState('');
  const [advice, setAdvice] = useState('');
  const [loading, setLoading] = useState(false);
  const [adviceLoading, setAdviceLoading] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('lastResult');
    if (saved) {
      setResult(saved);
    }
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result.split(',')[1]);
    };
    if (file) reader.readAsDataURL(file);
  };

  const handleAnalyze = async () => {
    if (!image) return alert('Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌï¥Ï£ºÏÑ∏Ïöî.');
    setLoading(true);
    setAdvice('');
    try {
      const response = await fetch('http://127.0.0.1:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption: '' })
      });
      const data = await response.json();
      setResult(data.result);
      localStorage.setItem('lastResult', data.result);
    } catch (err) {
      alert('Î∂ÑÏÑù Ïã§Ìå®');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleAdvice = async (type) => {
    setAdviceLoading(true);
    setAdviceType(type);
    try {
      const response = await fetch('http://127.0.0.1:5000/advice', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption: '', adviceType: type })
      });
      const data = await response.json();
      const cleaned = data.result.toLowerCase().includes("Ï£ÑÏÜ°") ? '' : data.result;
      setAdvice(cleaned);
    } catch (err) {
      setAdvice('‚ùå Ï°∞Ïñ∏ ÏöîÏ≤≠Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.');
      console.error(err);
    } finally {
      setAdviceLoading(false);
    }
  };

  const extractScore = (text) => {
    const match = text.match(/Ïã´Ïñ¥ÌïòÏßÄ ÏïäÏùÑ ÌôïÎ•†:\s*(\d+)/);
    return match ? parseInt(match[1], 10) : null;
  };

  const getEmojiFeedback = (score) => {
    if (score >= 90) return "üéâ Í±∞Ïùò Î™®Îì† ÏÇ¨ÎûåÎì§Ïù¥ Ï¢ãÏïÑÌï† Í±∞ÏòàÏöî!";
    if (score >= 70) return "üòä ÎåÄÎ∂ÄÎ∂Ñ Í∏çÏ†ïÏ†ÅÏúºÎ°ú Î≥º Í±∞ÏòàÏöî.";
    if (score >= 50) return "üôÇ Î¨¥ÎÇúÌïòÍ≤å Í¥úÏ∞ÆÏùÑ Í±∞ÏòàÏöî.";
    return "üòê Í±∞Î∂ÄÍ∞ê ÎäêÎÅºÎäî ÏÇ¨ÎûåÏù¥ ÏûàÏùÑ ÏàòÎèÑ ÏûàÏñ¥Ïöî.";
  };

  const handleCopyAdvice = () => {
    if (advice) {
      navigator.clipboard.writeText(advice);
      alert('‚úÖ Ï°∞Ïñ∏Ïù¥ ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-200 via-purple-300 to-indigo-400 flex items-center justify-center px-4 py-12 text-gray-800">
      <div className="bg-white rounded-3xl shadow-2xl p-10 max-w-md w-full">
        <h1 className="text-4xl font-extrabold text-center text-indigo-700 mb-2 tracking-tight">SafePost üåü</h1>
        <p className="text-center text-sm text-gray-500 mb-8">Predict how safely your followers will react to your post.</p>

        <label className="border-2 border-dashed border-gray-300 rounded-xl p-5 w-full text-center cursor-pointer hover:border-indigo-400 transition block">
          <input type="file" accept="image/*" onChange={handleImageChange} className="hidden" />
          {image ? '‚úÖ Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù ÏôÑÎ£å' : 'üì∑ Ïó¨Í∏∞Ïóê Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌï¥Î≥¥ÏÑ∏Ïöî'}
        </label>

        {image && (
          <div className="mt-6 rounded-xl overflow-hidden shadow-md animate-fade-in">
            <img
              src={`data:image/jpeg;base64,${image}`}
              alt="ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ"
              className="w-full object-cover max-h-96"
            />
          </div>
        )}

        <button
          onClick={handleAnalyze}
          disabled={loading}
          className="mt-6 bg-indigo-600 hover:bg-indigo-700 text-white w-full py-3 rounded-xl text-lg font-semibold transition"
        >
          {loading ? 'Î∂ÑÏÑù Ï§ë...' : '‚ú® Î∂ÑÏÑùÌïòÍ∏∞'}
        </button>

        {result && (
          <div className="mt-8 bg-gray-100 p-5 rounded-xl">
            <h3 className="text-xl font-semibold mb-3">üìä Î∂ÑÏÑù Í≤∞Í≥º:</h3>
            <pre className="whitespace-pre-wrap text-sm font-medium text-gray-700">{result}</pre>

            {(() => {
              const score = extractScore(result);
              if (score !== null) {
                return (
                  <div className="mt-5 text-base font-medium text-indigo-700">
                    üëâ <span>{getEmojiFeedback(score)}</span>
                    <button
                      onClick={() => {
                        setResult('');
                        setAdvice('');
                        localStorage.removeItem('lastResult');
                      }}
                      className="block mt-4 px-4 py-2 bg-red-100 hover:bg-red-200 rounded-md text-sm text-red-600"
                    >
                      üóëÔ∏è Í≤∞Í≥º Ï¥àÍ∏∞Ìôî
                    </button>
                  </div>
                );
              } else {
                return null;
              }
            })()}

            <div className="mt-6">
              <h4 className="text-sm font-semibold mb-2">üí¨ Îçî Í∂ÅÍ∏àÌïú Í≤å ÏûàÎÇòÏöî?</h4>
              <div className="flex flex-wrap gap-2">
                <button onClick={() => handleAdvice('overall')} className="px-3 py-1 bg-indigo-100 hover:bg-indigo-200 text-sm rounded">Ï†ÑÏ≤¥ Ï°∞Ïñ∏</button>
                <button onClick={() => handleAdvice('image')} className="px-3 py-1 bg-indigo-100 hover:bg-indigo-200 text-sm rounded">Ïù¥ÎØ∏ÏßÄ Í¥ÄÎ†®</button>
                <button onClick={() => handleAdvice('caption')} className="px-3 py-1 bg-indigo-100 hover:bg-indigo-200 text-sm rounded">Ï∫°ÏÖò Í¥ÄÎ†®</button>
              </div>
            </div>
          </div>
        )}

        {(advice || adviceLoading) && (
          <div className="mt-6 bg-yellow-100 border-l-4 border-yellow-400 text-yellow-800 p-4 rounded-xl">
            <div className="flex justify-between items-center mb-2">
              <h4 className="font-semibold">üí° {adviceType === 'caption' ? 'Ï∫°ÏÖò Í¥ÄÎ†® Ï°∞Ïñ∏' : adviceType === 'image' ? 'Ïù¥ÎØ∏ÏßÄ Í¥ÄÎ†® Ï°∞Ïñ∏' : 'Î∞òÏùëÏùÑ ÎÜíÏù¥Í∏∞ ÏúÑÌïú Ï°∞Ïñ∏'}</h4>
              {!adviceLoading && advice && (
                <button
                  onClick={handleCopyAdvice}
                  className="text-xs px-2 py-1 bg-indigo-200 hover:bg-indigo-300 rounded"
                >
                  Î≥µÏÇ¨
                </button>
              )}
            </div>
            <pre className="text-sm whitespace-pre-wrap">
              {adviceLoading ? '‚è≥ Ï°∞Ïñ∏ Î∂àÎü¨Ïò§Îäî Ï§ë...' : advice || '‚ö†Ô∏è Ïú†Ìö®Ìïú Ï°∞Ïñ∏Ïù¥ ÏóÜÏäµÎãàÎã§.'}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
