import React, { useState } from 'react';
import './index.css';

function App() {
  // Î™®Îìú: 'analyze' ÎòêÎäî 'risk'
  const [mode, setMode] = useState('analyze');

  // Í≥µÌÜµ ÏÉÅÌÉú
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Í∏∞Î≥∏ Î∂ÑÏÑù Í≤∞Í≥º
  const [result, setResult] = useState('');

  // ÏßÄÏù∏ Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä ÏÉÅÌÉú
  const [targetId, setTargetId] = useState('');
  const [riskResult, setRiskResult] = useState('');

  // Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleImageChange = e => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.type.startsWith('image/jpeg') && !file.type.startsWith('image/png')) {
      alert('JPG ÎòêÎäî PNG Ïù¥ÎØ∏ÏßÄÎßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    const reader = new FileReader();
    reader.onloadend = () => {
      const base64 = reader.result.split(',')[1];
      setImage(base64);
    };
    reader.readAsDataURL(file);
  };

  // Í∏∞Î≥∏ Î∂ÑÏÑù API Ìò∏Ï∂ú
  const handleAnalyze = async () => {
    if (!image) return alert('Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌï¥Ï£ºÏÑ∏Ïöî.');
    setLoading(true);
    setError('');
    setResult('');
    try {
      const res = await fetch('https://safepost.onrender.com/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption })
      });
      const data = await res.json();
      console.log('[analyze ÏùëÎãµ]', data);
      if (!res.ok) throw new Error(data.error || 'Î∂ÑÏÑù Ïã§Ìå®');
      setResult(data.result);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // ÏßÄÏù∏ Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä API Ìò∏Ï∂ú
  const handleRiskAssess = async () => {
    if (!image) return alert('Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌï¥Ï£ºÏÑ∏Ïöî.');
    if (!targetId) return alert('ÏßÄÏù∏ IDÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    setLoading(true);
    setError('');
    setRiskResult('');
    try {
      const res = await fetch('https://safepost.onrender.com/risk_assess', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption, target_user_id: targetId })
      });
      const data = await res.json();
      console.log('[risk_assess ÏùëÎãµ]', data);
      if (!res.ok) throw new Error(data.error || 'Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä Ïã§Ìå®');
      setRiskResult(data.risk_assessment);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Î∂ÑÏÑù Í≤∞Í≥º ÌååÏã±
  const extractAnalysis = text => {
    const scoreMatch = text.match(/Ïã´Ïñ¥ÌïòÏßÄ ÏïäÏùÑ ÌôïÎ•†:\s*(\d+)/);
    const warningMatch = text.match(/Í≤ΩÍ≥†:\s*(.*)/);
    const recommendationMatch = text.match(/Ï∂îÏ≤ú:\s*(.*)/);
    return {
      score: scoreMatch ? parseInt(scoreMatch[1], 10) : null,
      warning: warningMatch ? warningMatch[1].trim() : null,
      recommendation: recommendationMatch ? recommendationMatch[1].trim() : null
    };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-200 via-purple-300 to-blue-300 flex items-center justify-center px-4 py-12 text-gray-800">
      <div className="bg-white rounded-3xl shadow-2xl p-10 max-w-md w-full">
        <h1 className="text-4xl font-extrabold text-center text-indigo-700 mb-2 tracking-tight">SafePost üåü</h1>
        <p className="text-center text-sm text-gray-500 mb-8">ÏóÖÎ°úÎìú Ï†ÑÏóê, ÎãπÏã†Ïùò Í≤åÏãúÎ¨ºÏùÑ ÏïàÏ†ÑÌïòÍ≤å Ï†êÍ≤ÄÌïòÏÑ∏Ïöî.</p>

        {/* Î™®Îìú ÏÑ†ÌÉù Î≤ÑÌäº */}
        <div className="flex justify-center mb-6 space-x-4">
          <button
            onClick={() => setMode('analyze')}
            className={`px-4 py-2 rounded-full ${mode === 'analyze' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700'}`}
          >
            Í∏∞Î≥∏ Î∂ÑÏÑù
          </button>
          <button
            onClick={() => setMode('risk')}
            className={`px-4 py-2 rounded-full ${mode === 'risk' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700'}`}
          >
            ÏßÄÏù∏ Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä
          </button>
        </div>

        {/* Í≥µÌÜµ: Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú */}
        <label className="border-2 border-dashed border-gray-300 rounded-xl p-5 w-full text-center cursor-pointer hover:border-indigo-400 transition block">
          <input type="file" accept="image/*" onChange={handleImageChange} className="hidden" />
          {image ? '‚úÖ Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù ÏôÑÎ£å' : 'üì∑ Ïó¨Í∏∞Ïóê Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌï¥Î≥¥ÏÑ∏Ïöî'}
        </label>

        {/* Ï∫°ÏÖò ÏûÖÎ†• */}
        <textarea
          className="border p-2 mt-4 rounded-xl w-full"
          rows={2}
          placeholder="Ï∫°ÏÖò ÏûÖÎ†• (ÏÑ†ÌÉù)"
          value={caption}
          onChange={e => setCaption(e.target.value)}
        />

        {/* ÏßÄÏù∏ ID ÏûÖÎ†•: risk Î™®ÎìúÏóêÎßå ÌëúÏãú */}
        {mode === 'risk' && (
          <input
            className="border p-2 mt-4 rounded-xl w-full"
            placeholder="ÏßÄÏù∏ ID ÏûÖÎ†•"
            value={targetId}
            onChange={e => setTargetId(e.target.value)}
          />
        )}

        {/* Ïã§Ìñâ Î≤ÑÌäº */}
        <button
          onClick={mode === 'analyze' ? handleAnalyze : handleRiskAssess}
          disabled={loading}
          className="mt-6 bg-indigo-600 hover:bg-indigo-700 text-white w-full py-3 rounded-xl text-lg font-semibold transition"
        >
          {loading
            ? mode === 'analyze' ? 'Î∂ÑÏÑù Ï§ë...' : 'ÌèâÍ∞Ä Ï§ë...'
            : mode === 'analyze' ? '‚ú® Î∂ÑÏÑùÌïòÍ∏∞' : 'üîç ÏúÑÌóòÎèÑ ÌèâÍ∞ÄÌïòÍ∏∞'
          }
        </button>

        {/* ÏóêÎü¨ Î©îÏãúÏßÄ */}
        {error && (
          <p className="mt-4 text-red-500">‚ö†Ô∏è {error}</p>
        )}

        {/* Í∏∞Î≥∏ Î∂ÑÏÑù Í≤∞Í≥º */}
        {mode === 'analyze' && result && (
          <div className="mt-8 bg-gray-100 p-5 rounded-xl">
            <h3 className="text-xl font-semibold mb-3">üìä Î∂ÑÏÑù Í≤∞Í≥º</h3>
            <pre className="whitespace-pre-wrap text-sm font-medium text-gray-700">
              {(() => {
                const { score, warning, recommendation } = extractAnalysis(result);
                if (score !== null && warning && recommendation) {
                  return `üìä Ïù¥ Í≤åÏãúÎ¨ºÏùÄ ${score}% ÌôïÎ•†Î°ú ÏïàÏ†ÑÌï©ÎãàÎã§.\nüõë Í≤ΩÍ≥†: ${warning}\n‚ú® Ï∂îÏ≤ú: ${recommendation}`;
                }
                return 'Í≤∞Í≥ºÎ•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.';
              })()}
            </pre>
          </div>
        )}

        {/* ÏßÄÏù∏ Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä Í≤∞Í≥º */}
        {mode === 'risk' && riskResult && (
          <div className="mt-8 bg-gray-100 p-5 rounded-xl whitespace-pre-wrap text-sm">
            <h3 className="text-xl font-semibold mb-3">üîç ÏßÄÏù∏ Î¶¨Ïä§ÌÅ¨ ÌèâÍ∞Ä Í≤∞Í≥º</h3>
            <pre>{riskResult}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
