      <button onClick={handleAnalyze} disabled={loading}>
        {loading ? 'Î∂ÑÏÑù Ï§ë...' : 'Î∂ÑÏÑùÌïòÍ∏∞'}
      </button>
      {/* Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ */}
{image && (
  <div style={{ marginTop: '1.5rem' }}>
