      const response = await fetch('http://127.0.0.1:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption: '' })
      });
      const data = await response.json();
      setResult(data.result);
    } catch (err) {
      alert('Î∂ÑÏÑù Ïã§Ìå®');
      console.error(err);
    } finally {
