import React, { useState, useEffect } from 'react';
import './index.css';

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState('');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('lastResult');
    if (saved) {
      setResult(saved);
    }
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result.split(',')[1]);
    };
    if (file) reader.readAsDataURL(file);
  };

  const handleAnalyze = async () => {
    if (!image || !caption) return alert('이미지와 캡션을 모두 입력해주세요.');
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption })
      });
      const data = await response.json();
      setResult(data.result);
      localStorage.setItem('lastResult', data.result);
    } catch (err) {
      alert('분석 실패');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const extractScore = (text) => {
    const match = text.match(/확률:\s*(\d+)/);
    return match ? parseInt(match[1], 10) : null;
  };

  const getEmojiFeedback = (score) => {
    if (score >= 90) return "🥵 좋아요 폭주 예상!";
    if (score >= 70) return "😍 반응 좋을 듯해요!";
    if (score >= 50) return "🤔 그럭저럭 무난한 반응?";
    return "😢 반응이 아쉬울 수도 있어요";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-200 via-purple-300 to-indigo-400 flex items-center justify-center px-4 py-10 text-gray-800">
      <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full">
        <h1 className="text-3xl font-bold mb-2 text-center">Help by Post</h1>
        <p className="text-center text-sm text-gray-500 mb-6">Predict how your followers will respond to your post.</p>

        <label className="border border-gray-300 rounded-lg p-3 w-full text-center cursor-pointer hover:border-blue-400 transition block">
          <input type="file" accept="image/*" onChange={handleImageChange} className="hidden" />
          {image ? '✅ 이미지 선택 완료' : '이미지를 업로드하려면 클릭하세요'}
        </label>

        {image && (
          <div className="mt-4 rounded-lg overflow-hidden shadow-md animate-fade-in">
            <img
              src={`data:image/jpeg;base64,${image}`}
              alt="업로드된 이미지"
              className="w-full object-cover max-h-96"
            />
          </div>
        )}

        <textarea
          placeholder="캡션을 입력하세요"
          rows={4}
          className="mt-4 w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          value={caption}
          onChange={(e) => setCaption(e.target.value)}
        />

        <button
          onClick={handleAnalyze}
          disabled={loading}
          className="mt-4 bg-black hover:bg-gray-800 text-white w-full py-2 rounded-lg transition"
        >
          {loading ? '분석 중...' : '분석하기'}
        </button>

        {result && (
          <div className="mt-6 bg-gray-100 p-4 rounded-lg">
            <h3 className="text-lg font-semibold mb-2">📊 분석 결과:</h3>
            <pre className="whitespace-pre-wrap text-sm">{result}</pre>

            {(() => {
              const score = extractScore(result);
              if (score !== null) {
                return (
                  <div className="mt-4 text-base font-medium">
                    👉 <span>{getEmojiFeedback(score)}</span>

                    <button
                      onClick={() => {
                        setResult('');
                        localStorage.removeItem('lastResult');
                      }}
                      className="block mt-4 px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-md text-sm"
                    >
                      🗑️ 결과 초기화
                    </button>
                  </div>
                );
              } else {
                return null;
              }
            })()}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
