import React, { useState } from 'react';

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState('');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  // Ïù¥ÎØ∏ÏßÄ base64Î°ú Î≥ÄÌôòÌïòÎäî Ìï®Ïàò
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result.split(',')[1]); // data:image/... Î∂ÄÎ∂Ñ Ï†úÍ±∞
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // GPT ÏÑúÎ≤ÑÎ°ú Î∂ÑÏÑù ÏöîÏ≤≠ Î≥¥ÎÇ¥Í∏∞
  const handleAnalyze = async () => {
    if (!image || !caption) return alert('Ïù¥ÎØ∏ÏßÄÏôÄ Ï∫°ÏÖòÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!');
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ image, caption })
      });
      const data = await response.json();
      setResult(data.result);
    } catch (err) {
      console.error(err);
      alert('Î∂ÑÏÑù Ïã§Ìå® üò≠');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: 'auto' }}>
      <h1>Ïù∏Ïä§ÌÉÄ Í∞êÏÑ± Î∂ÑÏÑùÍ∏∞ üåà</h1>

      <input type="file" accept="image/*" onChange={handleImageChange} /><br /><br />
      <textarea
        placeholder="Ï∫°ÏÖòÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
        rows={4}
        style={{ width: '100%' }}
        value={caption}
        onChange={(e) => setCaption(e.target.value)}
      /><br /><br />

      <button onClick={handleAnalyze} disabled={loading}>
        {loading ? 'Î∂ÑÏÑù Ï§ë...' : 'Î∂ÑÏÑùÌïòÍ∏∞'}
      </button>

      {result && (
  <div style={{ marginTop: '2rem', background: '#f2f2f2', padding: '1rem' }}>
    <h3>üìä Î∂ÑÏÑù Í≤∞Í≥º:</h3>
    <pre style={{ whiteSpace: 'pre-wrap', fontSize: '1.1rem' }}>{result}</pre>
  </div>
)}

    </div>
  );
}

export default App;
