import React, { useState, useEffect } from 'react';
import './index.css';

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState('');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('lastResult');
    if (saved) {
      setResult(saved);
    }
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result.split(',')[1]);
    };
    if (file) reader.readAsDataURL(file);
  };

  const handleAnalyze = async () => {
    if (!image || !caption) return alert('이미지와 캡션을 모두 입력해주세요.');
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image, caption })
      });
      const data = await response.json();
      setResult(data.result);
      localStorage.setItem('lastResult', data.result);
    } catch (err) {
      alert('분석 실패');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const extractScore = (text) => {
    const match = text.match(/확률:\s*(\d+)/);
    return match ? parseInt(match[1], 10) : null;
  };

  const getEmojiFeedback = (score) => {
    if (score >= 90) return "🥵 좋아요 폭주 예상!";
    if (score >= 70) return "😍 반응 좋을 듯해요!";
    if (score >= 50) return "🤔 그럭저럭 무난한 반응?";
    return "😢 반응이 아쉬울 수도 있어요";
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center justify-start px-4 py-10 text-gray-800">
      <h1 className="text-3xl font-bold mb-6 text-center">인스타 감성 분석기 🌈</h1>

      <label className="border-2 border-dashed border-gray-400 rounded-lg p-6 w-full max-w-md text-center cursor-pointer hover:border-blue-400 transition">
        <input type="file" accept="image/*" onChange={handleImageChange} className="hidden" />
        {image ? '✅ 이미지 선택 완료' : '이미지를 업로드하려면 클릭하세요'}
      </label>

      <textarea
        placeholder="캡션을 입력하세요"
        rows={4}
        className="mt-4 w-full max-w-md p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        value={caption}
        onChange={(e) => setCaption(e.target.value)}
      />

      <button
        onClick={handleAnalyze}
        disabled={loading}
        className="mt-4 bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg transition"
      >
        {loading ? '분석 중...' : '분석하기'}
      </button>

      {image && (
        <div className="mt-6 w-full max-w-md">
          <h4 className="text-lg font-semibold mb-2">🖼️ 업로드한 이미지 미리보기:</h4>
          <img
            src={`data:image/jpeg;base64,${image}`}
            alt="업로드한 이미지"
            className="w-full rounded-lg shadow-md"
          />
        </div>
      )}

      {result && (
        <div className="mt-8 w-full max-w-md bg-white shadow-lg rounded-lg p-6">
          <h3 className="text-xl font-semibold mb-3">📊 분석 결과:</h3>
          <pre className="whitespace-pre-wrap text-sm">{result}</pre>

          {(() => {
            const score = extractScore(result);
            if (score !== null) {
              return (
                <div className="mt-4 text-base font-medium">
                  👉 <span>{getEmojiFeedback(score)}</span>

                  <button
                    onClick={() => {
                      setResult('');
                      localStorage.removeItem('lastResult');
                    }}
                    className="block mt-4 px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-md text-sm"
                  >
                    🗑️ 결과 초기화
                  </button>
                </div>
              );
            } else {
              return null;
            }
          })()}
        </div>
      )}
    </div>
  );
}

export default App;
